// â”€â”€â”€ Chat Message Component â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function ChatMessage({ message }) {
    const formatText = (text) => {
        return text
            .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
            .replace(/\n/g, '<br/>');
    };

    return (
        <div className={`chatbot-msg chatbot-msg--${message.sender}`}>
            <div className="chatbot-msg__avatar">
                {message.sender === 'bot' ? <Bot size={16} /> : <User size={16} />}
            </div>
            <div className="chatbot-msg__bubble">
                <div
                    className="chatbot-msg__text"
                    dangerouslySetInnerHTML={{ __html: formatText(message.text) }}
                />
                {message.timestamp && (
                    <span className="chatbot-msg__time">
                        {new Date(message.timestamp).toLocaleTimeString('en-IN', { hour: '2-digit', minute: '2-digit' })}
                    </span>
                )}
            </div>
        </div>
    );
}

// â”€â”€â”€ Main Chatbot Component â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// â”€â”€â”€ Generate role-aware welcome message â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function getWelcomeMessage(user) {
    if (!user) {
        return "Welcome to **Artium Gallery**! ðŸŽ¨ I'm your art assistant. Ask me about artworks, artists, exhibitions, or let me help you navigate!";
    }
    const name = user.name || user.email.split('@')[0];
    switch (user.role) {
        case 'artist':
            return `Welcome back, **${name}**! ðŸŽ¨ I'm your artist assistant. I can help you upload artworks, track sales, manage your portfolio, or answer any questions!`;
        case 'visitor':
            return `Welcome back, **${name}**! ðŸ‘‹ I'm your personal art guide. I can help you browse artworks, manage your cart, find deals, or explore exhibitions!`;
        case 'curator':
            return `Welcome back, **${name}**! ðŸ›ï¸ I'm your curator assistant. I can help you manage exhibitions, review artworks, and explore the gallery!`;
        case 'admin':
            return `Welcome back, **${name}**! âš™ï¸ I'm your admin assistant. I can help you manage the platform, users, and operations.`;
        default:
            return `Welcome back, **${name}**! ðŸŽ¨ I'm your art assistant. How can I help you today?`;
    }
}

export default function Chatbot() {
    const [isOpen, setIsOpen] = useState(false);
    const { user } = useAuth();
    const [messages, setMessages] = useState([
        {
            id: 1,
            sender: 'bot',
            text: getWelcomeMessage(user),
            timestamp: new Date().getTime()
        }
    ]);

    // Update welcome message when user logs in/out
    const prevUserRef = useRef(user);
    useEffect(() => {
        if (prevUserRef.current !== user) {
            prevUserRef.current = user;
            setMessages([{
                id: Date.now(),
                sender: 'bot',
                text: getWelcomeMessage(user),
                timestamp: Date.now()
            }]);
        }
    }, [user]);
    const [input, setInput] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const messagesEndRef = useRef(null);
    const inputRef = useRef(null);
    const navigate = useNavigate();
    const { cartItems, addToCart } = useCart();

    const scrollToBottom = useCallback(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, []);

    useEffect(() => {
        scrollToBottom();
    }, [messages, isTyping, scrollToBottom]);

    useEffect(() => {
        if (isOpen && inputRef.current) {
            inputRef.current.focus();
        }
    }, [isOpen]);

    const handleSend = useCallback((text) => {
        const messageText = text || input.trim();
        if (!messageText) return;

        // Add user message
        const userMsg = { id: Date.now(), sender: 'user', text: messageText, timestamp: Date.now() };
        setMessages(prev => [...prev, userMsg]);
        setInput('');
        setIsTyping(true);

        // Simulate thinking delay
        setTimeout(() => {
            const response = generateResponse(messageText, { user, cartItems, navigate, addToCart });

            const botMsg = {
                id: Date.now() + 1,
                sender: 'bot',
                text: response.text,
                suggestions: response.suggestions,
                action: response.action,
                timestamp: Date.now()
            };

            setMessages(prev => [...prev, botMsg]);
            setIsTyping(false);

            // Auto-navigate if action exists
            if (response.action?.type === 'navigate') {
                setTimeout(() => navigate(response.action.path), 1500);
            }
        }, 600 + Math.random() * 800);
    }, [input, user, cartItems, navigate, addToCart]);

    const handleKeyDown = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSend();
        }
    };

    const handleSuggestionClick = (suggestion) => {
        handleSend(suggestion);
    };

    const getQuickActions = () => {
        if (user?.role === 'artist') {
            return [
                { icon: Palette, label: 'Upload Art', query: 'Upload new artwork' },
                { icon: Image, label: 'My Artworks', query: 'View my artworks' },
                { icon: Map, label: 'Dashboard', query: 'My dashboard' },
                { icon: ShoppingCart, label: 'Sales', query: 'Track my sales' },
            ];
        }
        if (user?.role === 'visitor') {
            return [
                { icon: Palette, label: 'Artworks', query: 'Browse artworks' },
                { icon: ShoppingCart, label: 'My Cart', query: 'My cart' },
                { icon: Image, label: 'Featured', query: 'Show featured art' },
                { icon: Map, label: 'Tour', query: 'Start virtual tour' },
            ];
        }
        if (user?.role === 'curator') {
            return [
                { icon: Map, label: 'Dashboard', query: 'My dashboard' },
                { icon: Image, label: 'Exhibitions', query: 'View exhibitions' },
                { icon: Palette, label: 'Artworks', query: 'Browse artworks' },
                { icon: ShoppingCart, label: 'Artists', query: 'Show artists' },
            ];
        }
        if (user?.role === 'admin') {
            return [
                { icon: Map, label: 'Admin Panel', query: 'Admin dashboard' },
                { icon: Palette, label: 'Artworks', query: 'Browse artworks' },
                { icon: Image, label: 'Exhibitions', query: 'View exhibitions' },
                { icon: ShoppingCart, label: 'Artists', query: 'Show artists' },
            ];
        }
        return [
            { icon: Palette, label: 'Artworks', query: 'Browse artworks' },
            { icon: Image, label: 'Featured', query: 'Show featured art' },
            { icon: Map, label: 'Tour', query: 'Start virtual tour' },
            { icon: ShoppingCart, label: 'Cart', query: 'My cart' },
        ];
    };

    const quickActions = getQuickActions();

    return (
        <>
            {/* Floating Chat Button */}
            <button
                className={`chatbot-toggle ${isOpen ? 'chatbot-toggle--open' : ''}`}
                onClick={() => setIsOpen(!isOpen)}
                aria-label="Chat with us"
            >
                {isOpen ? <X size={24} /> : <MessageCircle size={24} />}
                {!isOpen && <span className="chatbot-toggle__pulse" />}
            </button>

            {/* Chat Window */}
            {isOpen && (
                <div className="chatbot-window">
                    {/* Header */}
                    <div className="chatbot-header">
                        <div className="chatbot-header__info">
                            <div className="chatbot-header__avatar">
                                <Sparkles size={18} />
                            </div>
                            <div>
                                <h4 className="chatbot-header__title">Artium Assistant</h4>
                                <span className="chatbot-header__status">
                                    <span className="chatbot-header__dot" /> Online
                                </span>
                            </div>
                        </div>
                        <button className="chatbot-header__close" onClick={() => setIsOpen(false)}>
                            <X size={18} />
                        </button>
                    </div>

                    {/* Quick Actions */}
                    <div className="chatbot-quick-actions">
                        {quickActions.map((action, i) => (
                            <button
                                key={i}
                                className="chatbot-quick-action"
                                onClick={() => handleSend(action.query)}
                            >
                                <action.icon size={14} />
                                <span>{action.label}</span>
                            </button>
                        ))}
                    </div>

                    {/* Messages */}
                    <div className="chatbot-messages">
                        {messages.map((msg) => (
                            <div key={msg.id}>
                                <ChatMessage message={msg} />
                                {msg.suggestions && msg.suggestions.length > 0 && msg.sender === 'bot' && (
                                    <div className="chatbot-suggestions">
                                        {msg.suggestions.map((s, i) => (
                                            <button
                                                key={i}
                                                className="chatbot-suggestion"
                                                onClick={() => handleSuggestionClick(s)}
                                            >
                                                {s} <ArrowRight size={12} />
                                            </button>
                                        ))}
                                    </div>
                                )}
                            </div>
                        ))}
                        {isTyping && (
                            <div className="chatbot-msg chatbot-msg--bot">
                                <div className="chatbot-msg__avatar"><Bot size={16} /></div>
                                <div className="chatbot-msg__bubble chatbot-typing">
                                    <span /><span /><span />
                                </div>
                            </div>
                        )}
                        <div ref={messagesEndRef} />
                    </div>

                    {/* Input */}
                    <div className="chatbot-input-area">
                        <input
                            ref={inputRef}
                            type="text"
                            className="chatbot-input"
                            placeholder="Ask me anything about art..."
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            onKeyDown={handleKeyDown}
                        />
                        <button
                            className="chatbot-send"
                            onClick={() => handleSend()}
                            disabled={!input.trim()}
                        >
                            <Send size={18} />
                        </button>
                    </div>
                </div>
            )}
        </>
    );
}

import { Link, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Heart, Eye, ExternalLink, ShoppingCart } from 'lucide-react';
import { useCart } from '../../context/CartContext';
import { useAuth } from '../../context/AuthContext';
import { useWishlist } from '../../context/WishlistContext';
import './ArtworkCard.css';

export default function ArtworkCard({ artwork, viewMode = 'grid' }) {
    const { addToCart, isInCart } = useCart();
    const { toggleWishlist, isInWishlist } = useWishlist();
    const { user } = useAuth();
    const navigate = useNavigate();
    const inCart = isInCart(artwork.id);
    const inWishlist = isInWishlist(artwork.id);

    const handleToggleWishlist = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (!user) {
            navigate('/login');
            return;
        }
        toggleWishlist(artwork);
    };

    const handleAddToCart = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (!user) {
            navigate('/login');
            return;
        }
        if (!inCart && artwork.available) {
            addToCart(artwork);
        }
    };

    if (viewMode === 'list') {
        return (
            <Link to={`/artwork/${artwork.id}`} className="artwork-card artwork-card--list">
                <div className="artwork-card__image-wrapper">
                    <img
                        src={artwork.thumbnail}
                        alt={artwork.title}
                        className="artwork-card__image"
                        loading="lazy"
                    />
                </div>
                <div className="artwork-card__content">
                    <div className="artwork-card__main">
                        <span className="artwork-card__category">{artwork.category}</span>
                        <h3 className="artwork-card__title">{artwork.title}</h3>
                        <p className="artwork-card__artist">by {artwork.artist}</p>
                        <p className="artwork-card__description">{artwork.description}</p>
                    </div>
                    <div className="artwork-card__details">
                        <div className="artwork-card__meta">
                            <span>{artwork.year}</span>
                            <span>{artwork.medium}</span>
                        </div>
                        <div className="artwork-card__stats">
                            <span><Eye size={14} /> {artwork.views.toLocaleString()}</span>
                            <span><Heart size={14} /> {artwork.likes.toLocaleString()}</span>
                        </div>
                        <div className="artwork-card__price">
                            {`â‚¹${artwork.price.toLocaleString('en-IN')}`}
                        </div>
                        <button
                            className={`artwork-card__cart-btn ${inCart ? 'in-cart' : ''}`}
                            onClick={handleAddToCart}
                            disabled={!artwork.available || inCart}
                        >
                            {inCart ? 'In Cart' : artwork.available ? 'Add to Cart' : 'Sold'}
                        </button>
                    </div>
                </div>
            </Link>
        );
    }

    return (
        <Link to={`/artwork/${artwork.id}`} className="artwork-card">
            <div className="artwork-card__image-wrapper">
                <img
                    src={artwork.thumbnail}
                    alt={artwork.title}
                    className="artwork-card__image"
                    loading="lazy"
                />

                {/* Overlay */}
                <div className="artwork-card__overlay">
                    <div className="artwork-card__actions">
                        <button
                            className={`artwork-card__action ${inWishlist ? 'active' : ''}`}
                            onClick={handleToggleWishlist}
                            aria-label={inWishlist ? "Remove from wishlist" : "Add to wishlist"}
                        >
                            <Heart size={18} fill={inWishlist ? "currentColor" : "none"} color={inWishlist ? "#e74c3c" : "currentColor"} />
                        </button>
                        <button className="artwork-card__action" aria-label="Quick view">
                            <ExternalLink size={18} />
                        </button>
                        <button
                            className={`artwork-card__action ${inCart ? 'active' : ''}`}
                            onClick={handleAddToCart}
                            aria-label="Add to cart"
                            disabled={!artwork.available}
                        >
                            <ShoppingCart size={18} />
                        </button>
                    </div>
                </div>

                {/* Badges */}
                <div className="artwork-card__badges">
                    {artwork.featured && (
                        <span className="artwork-card__badge">Featured</span>
                    )}
                    {!artwork.available && (
                        <span className="artwork-card__badge artwork-card__badge--sold">Sold</span>
                    )}
                </div>
            </div>

            <div className="artwork-card__content">
                <span className="artwork-card__category">{artwork.category}</span>
                <h3 className="artwork-card__title">{artwork.title}</h3>
                <p className="artwork-card__artist">{artwork.artist}</p>

                <div className="artwork-card__footer">
                    <div className="artwork-card__stats">
                        <span><Eye size={12} /> {artwork.views.toLocaleString()}</span>
                        <span><Heart size={12} /> {artwork.likes.toLocaleString()}</span>
                    </div>
                    <span className="artwork-card__price">
                        {`â‚¹${artwork.price.toLocaleString('en-IN')}`}
                    </span>
                </div>
            </div>
        </Link>
    );
}

import { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import {
    Menu,
    X,
    Sun,
    Moon,
    ShoppingCart,
    User,
    Search,
    ChevronDown,
    Package,
    Heart,
    MapPin,
    Bell,
    Gift,
    CreditCard,
    LogOut
} from 'lucide-react';
import { useTheme } from '../../context/ThemeContext';
import { useAuth } from '../../context/AuthContext';
import { useCart } from '../../context/CartContext';
import './Header.css';

export default function Header() {
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [isSearchOpen, setIsSearchOpen] = useState(false);
    const [isUserDropdownOpen, setIsUserDropdownOpen] = useState(false);
    const { theme, toggleTheme } = useTheme();
    const { user, isAuthenticated, logout } = useAuth();
    const { cartCount } = useCart();
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 50);
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    useEffect(() => {
        setIsMobileMenuOpen(false);
    }, [location]);

    const allNavLinks = [
        { path: '/gallery', label: 'Gallery' },
        { path: '/exhibitions', label: 'Exhibitions' },
        { path: '/virtual-tour', label: 'Virtual Tour' },
    ];

    // Hide all public links for artists and admins
    let navLinks = allNavLinks;
    if (isAuthenticated) {
        if (user?.role === 'artist' || user?.role === 'admin') {
            navLinks = [];
        }
    }

    const getDashboardLink = () => {
        if (!user) return '/login';
        switch (user.role) {
            case 'admin': return '/dashboard/admin';
            case 'artist': return '/dashboard/artist';
            case 'curator': return '/dashboard/curator';
            default: return '/dashboard/visitor';
        }
    };

    return (
        <header className={`header ${isScrolled ? 'header--scrolled' : ''}`}>
            <div className="header__container">
                {/* Logo */}
                <Link to="/" className="header__logo">
                    <motion.span
                        initial={{ opacity: 0, y: -20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5 }}
                        className="header__logo-text"
                    >
                        ARTIUM
                    </motion.span>
                    <span className="header__logo-tagline">Virtual Gallery</span>
                </Link>

                {/* Desktop Navigation */}
                <nav className="header__nav hide-mobile">
                    {navLinks.map((link, index) => (
                        <motion.div
                            key={link.path}
                            initial={{ opacity: 0, y: -20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ duration: 0.5, delay: index * 0.1 }}
                        >
                            <Link
                                to={link.path}
                                className={`header__nav-link ${location.pathname === link.path ? 'active' : ''}`}
                            >
                                {link.label}
                            </Link>
                        </motion.div>
                    ))}
                </nav>

                {/* Actions */}
                <div className="header__actions">
                    {/* Search */}
                    <button
                        className="header__action-btn"
                        onClick={() => setIsSearchOpen(!isSearchOpen)}
                        aria-label="Search"
                    >
                        <Search size={20} />
                    </button>

                    {/* Theme Toggle */}
                    <button
                        className="header__action-btn"
                        onClick={toggleTheme}
                        aria-label="Toggle theme"
                    >
                        <motion.div
                            initial={false}
                            animate={{ rotate: theme === 'dark' ? 180 : 0 }}
                            transition={{ duration: 0.3 }}
                        >
                            {theme === 'dark' ? <Sun size={20} /> : <Moon size={20} />}
                        </motion.div>
                    </button>

                    {/* Cart */}
                    <Link to="/cart" className="header__action-btn header__cart-btn">
                        <ShoppingCart size={20} />
                        {cartCount > 0 && (
                            <motion.span
                                className="header__cart-badge"
                                initial={{ scale: 0 }}
                                animate={{ scale: 1 }}
                                key={cartCount}
                            >
                                {cartCount}
                            </motion.span>
                        )}
                    </Link>

                    {/* User Menu */}
                    {isAuthenticated ? (
                        <div className="header__user-menu">
                            <button
                                className="header__user-btn"
                                onClick={() => setIsUserDropdownOpen(!isUserDropdownOpen)}
                            >
                                <img
                                    src={user.avatar}
                                    alt={user.name}
                                    className="header__user-avatar"
                                />
                                <span className="header__user-name hide-mobile">{user.name.split(' ')[0]}</span>
                                <ChevronDown size={16} className="hide-mobile" />
                            </button>

                            {/* User Dropdown */}
                            <AnimatePresence>
                                {isUserDropdownOpen && (
                                    <motion.div
                                        className="header__user-dropdown"
                                        initial={{ opacity: 0, y: -10 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        exit={{ opacity: 0, y: -10 }}
                                        transition={{ duration: 0.2 }}
                                    >
                                        {user?.role === 'visitor' ? (
                                            <>
                                                <div className="header__dropdown-header">
                                                    Your Account
                                                </div>
                                                <Link
                                                    to="/dashboard/visitor/profile"
                                                    className="header__dropdown-item"
                                                    onClick={() => setIsUserDropdownOpen(false)}
                                                >
                                                    <User size={18} />
                                                    <span>My Profile</span>
                                                </Link>
                                                <Link
                                                    to="/dashboard/visitor/profile?section=orders"
                                                    className="header__dropdown-item"
                                                    onClick={() => setIsUserDropdownOpen(false)}
                                                >
                                                    <Package size={18} />
                                                    <span>Orders</span>
                                                </Link>
                                                <Link
                                                    to="/dashboard/visitor/profile?section=wishlist"
                                                    className="header__dropdown-item"
                                                    onClick={() => setIsUserDropdownOpen(false)}
                                                >
                                                    <Heart size={18} />
                                                    <span>Wishlist</span>
                                                </Link>
                                                <Link
                                                    to="/dashboard/visitor/profile?section=cards"
                                                    className="header__dropdown-item"
                                                    onClick={() => setIsUserDropdownOpen(false)}
                                                >
                                                    <CreditCard size={18} />
                                                    <span>Saved Cards & Wallet</span>
                                                </Link>
                                                <Link
                                                    to="/dashboard/visitor/profile?section=addresses"
                                                    className="header__dropdown-item"
                                                    onClick={() => setIsUserDropdownOpen(false)}
                                                >
                                                    <MapPin size={18} />
                                                    <span>Saved Addresses</span>
                                                </Link>
                                                <Link
                                                    to="/dashboard/visitor/profile?section=notifications"
                                                    className="header__dropdown-item"
                                                    onClick={() => setIsUserDropdownOpen(false)}
                                                >
                                                    <Bell size={18} />
                                                    <span>Notifications</span>
                                                </Link>
                                                <Link
                                                    to="/dashboard/visitor/profile?section=giftcards"
                                                    className="header__dropdown-item"
                                                    onClick={() => setIsUserDropdownOpen(false)}
                                                >
                                                    <Gift size={18} />
                                                    <span>Gift Cards</span>
                                                </Link>
                                                <button
                                                    onClick={() => {
                                                        logout();
                                                        setIsUserDropdownOpen(false);
                                                        navigate('/');
                                                    }}
                                                    className="header__dropdown-item header__dropdown-logout"
                                                >
                                                    <LogOut size={18} />
                                                    <span>Logout</span>
                                                </button>
                                            </>
                                        ) : (
                                            <>
                                                <Link
                                                    to={getDashboardLink()}
                                                    className="header__dropdown-item"
                                                    onClick={() => setIsUserDropdownOpen(false)}
                                                >
                                                    <User size={18} />
                                                    <span>Dashboard</span>
                                                </Link>
                                                <button
                                                    onClick={() => {
                                                        logout();
                                                        setIsUserDropdownOpen(false);
                                                        navigate('/');
                                                    }}
                                                    className="header__dropdown-item header__dropdown-logout"
                                                >
                                                    <LogOut size={18} />
                                                    <span>Logout</span>
                                                </button>
                                            </>
                                        )}
                                    </motion.div>
                                )}
                            </AnimatePresence>
                        </div>
                    ) : (
                        <Link to="/login" className="header__action-btn header__login-btn hide-mobile">
                            <User size={20} />
                            <span>Login</span>
                        </Link>
                    )}

                    {/* Mobile Menu Toggle */}
                    <button
                        className="header__mobile-toggle"
                        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                        aria-label="Toggle menu"
                    >
                        {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
                    </button>
                </div>
            </div>

            {/* Search Bar */}
            <AnimatePresence>
                {isSearchOpen && (
                    <motion.div
                        className="header__search-bar"
                        initial={{ opacity: 0, height: 0 }}
                        animate={{ opacity: 1, height: 'auto' }}
                        exit={{ opacity: 0, height: 0 }}
                    >
                        <div className="header__search-container">
                            <Search size={20} className="header__search-icon" />
                            <input
                                type="text"
                                placeholder="Search artworks, artists, exhibitions..."
                                className="header__search-input"
                                autoFocus
                            />
                            <button
                                className="header__search-close"
                                onClick={() => setIsSearchOpen(false)}
                            >
                                <X size={20} />
                            </button>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Mobile Menu */}
            <AnimatePresence>
                {isMobileMenuOpen && (
                    <motion.div
                        className="header__mobile-menu"
                        initial={{ opacity: 0, x: '100%' }}
                        animate={{ opacity: 1, x: 0 }}
                        exit={{ opacity: 0, x: '100%' }}
                        transition={{ type: 'tween', duration: 0.3 }}
                    >
                        <nav className="header__mobile-nav">
                            {navLinks.map((link, index) => (
                                <motion.div
                                    key={link.path}
                                    initial={{ opacity: 0, x: 50 }}
                                    animate={{ opacity: 1, x: 0 }}
                                    transition={{ delay: index * 0.1 }}
                                >
                                    <Link
                                        to={link.path}
                                        className={`header__mobile-link ${location.pathname === link.path ? 'active' : ''}`}
                                    >
                                        {link.label}
                                    </Link>
                                </motion.div>
                            ))}

                            <div className="header__mobile-divider" />

                            {isAuthenticated ? (
                                <>
                                    {user?.role === 'visitor' ? (
                                        <Link to="/dashboard/visitor/profile" className="header__mobile-link">
                                            My Profile
                                        </Link>
                                    ) : (
                                        <Link to={getDashboardLink()} className="header__mobile-link">
                                            Dashboard
                                        </Link>
                                    )}
                                    <button onClick={() => { logout(); navigate('/'); }} className="header__mobile-link header__mobile-logout">
                                        Logout
                                    </button>
                                </>
                            ) : (
                                <>
                                    <Link to="/login" className="header__mobile-link">
                                        Login
                                    </Link>
                                    <Link to="/signup" className="header__mobile-link">
                                        Sign Up
                                    </Link>
                                </>
                            )}
                        </nav>
                    </motion.div>
                )}
            </AnimatePresence>
        </header>
    );
}

import React, { createContext, useContext, useState } from 'react';
import { artworks as initialArtworks } from '../data/mockData';

const ArtworkContext = createContext(null);

export function ArtworkProvider({ children }) {
    const [artworks, setArtworks] = useState(initialArtworks);

    const addArtwork = (newArtwork) => {
        // Generate a temporary ID (in a real app, this comes from the backend)
        const id = Math.max(...artworks.map(a => a.id), 0) + 1;

        const artworkWithDefaults = {
            ...newArtwork,
            id,
            featured: false,
            available: true,
            views: 0,
            likes: 0,
            // For images, if none provided or it's a file, we'll placeholder it for now
            // In a real app we'd upload the file and get a URL
            image: newArtwork.image || '/src/assets/pic1.jpg',
            thumbnail: newArtwork.image || '/src/assets/pic1.jpg'
        };

        setArtworks(prev => [artworkWithDefaults, ...prev]);
    };

    const toggleAvailability = (id) => {
        setArtworks(prev => prev.map(a =>
            a.id === id ? { ...a, available: !a.available } : a
        ));
    };

    const updateArtwork = (id, updatedFields) => {
        setArtworks(prev => prev.map(a =>
            a.id === id ? { ...a, ...updatedFields } : a
        ));
    };

    const deleteArtwork = (id) => {
        setArtworks(prev => prev.filter(a => a.id !== id));
    };

    const getArtworksByArtist = (artistName) => {
        return artworks.filter(a => a.artist === artistName);
    };

    return (
        <ArtworkContext.Provider value={{
            artworks,
            addArtwork,
            updateArtwork,
            deleteArtwork,
            toggleAvailability,
            getArtworksByArtist
        }}>
            {children}
        </ArtworkContext.Provider>
    );
}

export function useArtworks() {
    const context = useContext(ArtworkContext);
    if (!context) {
        throw new Error('useArtworks must be used within an ArtworkProvider');
    }
    return context;
}

