import { createContext, useContext, useState, useEffect } from 'react';

const WishlistContext = createContext();

export function WishlistProvider({ children }) {
    const [wishlistItems, setWishlistItems] = useState([]);

    // Load from local storage on mount
    useEffect(() => {
        const savedWishlist = localStorage.getItem('gallery-wishlist');
        if (savedWishlist) {
            try {
                setWishlistItems(JSON.parse(savedWishlist));
            } catch (e) {
                console.error("Error parsing wishlist from local storage", e);
            }
        }
    }, []);

    // Save to local storage whenever it changes
    useEffect(() => {
        localStorage.setItem('gallery-wishlist', JSON.stringify(wishlistItems));
    }, [wishlistItems]);

    const addToWishlist = (artwork) => {
        setWishlistItems(prev => {
            if (prev.find(item => item.id === artwork.id)) return prev;
            return [...prev, artwork];
        });
    };

    const removeFromWishlist = (id) => {
        setWishlistItems(prev => prev.filter(item => item.id !== id));
    };

    const toggleWishlist = (artwork) => {
        if (isInWishlist(artwork.id)) {
            removeFromWishlist(artwork.id);
        } else {
            addToWishlist(artwork);
        }
    };

    const isInWishlist = (id) => {
        return wishlistItems.some(item => item.id === id);
    };

    const clearWishlist = () => {
        setWishlistItems([]);
    };

    const wishlistCount = wishlistItems.length;

    return (
        <WishlistContext.Provider value={{
            wishlistItems,
            addToWishlist,
            removeFromWishlist,
            toggleWishlist,
            isInWishlist,
            clearWishlist,
            wishlistCount
        }}>
            {children}
        </WishlistContext.Provider>
    );
}

export const useWishlist = () => {
    const context = useContext(WishlistContext);
    if (!context) {
        throw new Error('useWishlist must be used within a WishlistProvider');
    }
    return context;
};

import { useState } from 'react';
