                            {/* Meta Info */}
                            <div className="exhibition-sidebar-card">
                                <div className="exhibition-sidebar-card__content">
                                    <h3>Exhibition Info</h3>
                                    <ul>
                                        <li>
                                            <Calendar size={20} />
                                            <div>
                                                <p className="label">Dates</p>
